mapper.select:
	select * from DEALS_BOARD d join BOOKS_INFO b on d.isbn = b.isbn
	
	
	    AND d.books_category_id = #{booksCategoryId}
			
    
        AND d.status = 'N' 
    
	order by deal_id DESC 
	limit #{skip}, #{size}

mapper.update:
update DEALS_BOARD
set disabled='Y'
where deal_id=#{dealId}

update DEALS_BOARD
set status=#{status}
where deal_id=#{dealId}
