sql.where:
			
			
			disabled ='N'	
			
					
			
				AND
 				
					
					
						concat_ws(' ', d.title, d.content, d.email, b.isbn, b.book_title, b.book_writer, b.book_publisher) like concat('%', #{keyword}, '%')
					
										
					
						concat_ws(' ', b.isbn, b.book_title, b.book_writer, b.book_publisher) like concat('%', #{keyword}, '%')
					
				
			
			
			    AND d.books_category_id = #{booksCategoryId}
					
		    
		        AND d.status = 'N' 
		    
		    
		        AND d.email = #{email} 
			  
		    
		        AND d.isbn = #{isbn}

mapper.select:
SELECT b.*, CASE WHEN f.favorite_id IS NOT NULL THEN 'Y' ELSE 'N' END as is_favorited FROM DEALS_BOARD b LEFT OUTER JOIN FAVORITES_DEALS f ON b.deal_id = f.deal_id AND f.disabled = 'N'
AND f.email = #{email}
WHERE b.deal_id = #{dealId} AND b.disabled = 'N'

select * from DEALS_BOARD d join BOOKS_INFO b on d.isbn = b.isbn

order by deal_id DESC 
limit #{skip}, #{size}

mapper.update:
update DEALS_BOARD
set disabled='Y'
where deal_id=#{dealId}

update DEALS_BOARD
set status=#{status}
where deal_id=#{dealId}

UPDATE USERS

    
        SET book_alert = book_alert + 1
    
    
        SET book_alert = book_alert - #{bookAlert}
    

WHERE email = #{email}

mapper.select:
SELECT email FROM FAVORITES_BOOKS
where isbn = #{isbn}
AND DISABLED = 'N'

mapper.update:
update DEALS_BOARD
	SET view_cnt= view_cnt+1
	where deal_id = #{dealId}
